import classes from './Cart.module.css'
import React from 'react'
import ReactDom from 'react-dom'
import Card from '../UI/Card'

const OverLay = props => {
    return <div className={classes.overLay}></div>
}

const CartModal = props => {
    const cartItems = <ul className={classes['cart-items']}>
        {[{id:'c1', name:'Sushi', amount:2, price:12.99}].map(item => <li key={item.id}>{item.name}</li>)}
        </ul>
    return  <Card className={classes.cart}>
                {cartItems}
                <div className={classes.total}>
                    <span>Total Amount</span>
                    <span>35.62</span>
                </div>
                <div className={classes.actions} > 
                    <button className={classes['button--alt']}>Close</button>
                    <button className={classes.button}>Order</button>
                </div>
            </Card>
}
const Cart = props => {
    return (
        <React.Fragment>
            {ReactDom.createPortal(<OverLay />, document.getElementById('overLay'))}
            {ReactDom.createPortal(<CartModal />, document.getElementById('cart-modal'))}
        </React.Fragment>
    )
}



export default Cart;
